import React, { Component } from 'react';
import Loader from './Loader/Loader';
//import Table from './Table/Table';

class App extends Component {
  state = {
    isLoading: true,
    data: [],
  }


  async componentDidMount() {
    const response = await fetch(` http://www.filltext.com/?rows=32&id={number|1000}&firstName={firstName}&lastName={lastName}&email={email}&phone={phone|(xxx)xxx-xx-xx}&address={addressObject}&description={lorem|32}`)
    const data = await response.json()
    this.setState({
      isLoading: false,
      data: data
    })
  }

  onSort = sortField => (
    console.log(sortField)
  )

  Table(){
    <table className="table">
        <thead>
            <tr>
                <th onClick={props.onSort.bind(null, 'id')}>ID</th>
                <th onClick={props.onSort.bind(null, 'firstName')}>First Name</th>
                <th onClick={props.onSort.bind(null, 'lastName')}>Last Name</th>
                <th onClick={props.onSort.bind(null, 'email')}>E-mail</th>
                <th onClick={props.onSort.bind(null, 'phone')}>Phone</th>
            </tr>
        </thead>
        <tbody>
            { props.data.map(item => (
                <tr key={item.id + item.phone}>
                    <td>{item.id}</td>
                    <td>{item.firstName}</td>
                    <td>{item.lastName}</td>
                    <td>{item.email}</td>
                    <td>{item.phone}</td>
                </tr>
            ))}
        </tbody>
    </table>
  }

  render() {
    return (
      <div className="container">
      {
        this.state.isLoading 
        ? <Loader />
        : <Table 
        data={this.state.data}
        onSort={this.onSort}
        />
      }
      </div>
    );
  }
}

export default App;
